function keyPressed() {
  if (keyCode === UP_ARROW) {
    movimiento = -velocidadpala;
  } else if (keyCode === DOWN_ARROW) {
    movimiento = velocidadpala;
  } else if (keyCode === LEFT_ARROW) {
    movimiento1 = -velocidadpala;
  } else if (keyCode === RIGHT_ARROW) {
    movimiento1 = velocidadpala;
  } else if (key === "2") {
    palaextra = !palaextra;
    if (playsolo) {
      playsolo = !playsolo;
    }
    pala3.yy = pala1.yy;
    pala4.yy = pala2.yy;
    reset();
  } else if (key === "1") {
    playsolo = !playsolo;
    if (palaextra) {
      palaextra = !palaextra;
    }
    reset();
  } else if (keyCode === 32) {
    start = !start;
  }
}

function keyReleased() {
  if (keyCode === UP_ARROW) {
    movimiento = 0;
  } else if (keyCode === DOWN_ARROW) {
    movimiento = 0;
  } else if (keyCode === LEFT_ARROW) {
    movimiento1 = 0;
  } else if (keyCode === RIGHT_ARROW) {
    movimiento1 = 0;
  }
}

